twemoji.parse(document.body),function(){var e=document.querySelectorAll(".js-audio audio");if(e)for(var t=0;t<e.length;t++){e[t].parentNode.querySelector("button").addEventListener("click",function(e){var t=document.querySelectorAll(".js-audio audio"),o=document.querySelectorAll(".js-audio button"),a=this.parentNode.querySelector("audio");if(0<a.duration&&!a.paused)this.classList.remove("playing"),a.pause();else{for(var r=0;r<o.length;r++)o[r].classList.remove("playing");for(r=0;r<t.length;r++)t[r].pause();this.classList.add("playing"),a.play()}e.preventDefault()})}}();